import React from 'react';
import { 
  LayoutDashboard, 
  Wallet, 
  Building2, 
  BarChart3, 
  Zap, 
  Gift, 
  Users, 
  BookOpen, 
  Phone,
  User,
  LogOut,
  Recycle
} from 'lucide-react';

const ModernSidebar = ({ activeItem = 'Dashboard', onItemClick, user }) => {
  const menuItems = [
    { id: 'Dashboard', label: 'Dashboard', icon: LayoutDashboard, path: '/dashboard' },
    { id: 'RewardsWallet', label: 'Rewards Wallet', icon: Wallet, path: '/rewards-wallet' },
    { id: 'CompanyResale', label: 'Company Resale', icon: Building2, path: '/company-resale' },
    { id: 'ImpactDashboard', label: 'Impact Dashboard', icon: BarChart3, path: '/impact-dashboard' },
    { id: 'Features', label: 'Features', icon: Zap, path: '/features' },
    { id: 'Rewards', label: 'Rewards', icon: Gift, path: '/rewards' },
    { id: 'Partnerships', label: 'Partners', icon: Users, path: '/partnerships' },
    { id: 'Blog', label: 'Blog', icon: BookOpen, path: '/blog' },
    { id: 'Contact', label: 'Contact', icon: Phone, path: '/contact' }
  ];

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  return (
    <div className="w-64 h-screen bg-slate-50 border-r border-slate-200 flex flex-col">
      {/* Header */}
      <div className="p-6 border-b border-slate-200">
        <div className="flex items-center space-x-3">
          <div className="w-8 h-8 bg-indigo-600 rounded-lg flex items-center justify-center">
            <Recycle className="h-5 w-5 text-white" />
          </div>
          <h1 className="text-xl font-bold text-slate-800">E-Waste Loop</h1>
        </div>
      </div>

      {/* Navigation */}
      <nav className="flex-1 p-4 space-y-1">
        {menuItems.map((item) => {
          const Icon = item.icon;
          const isActive = activeItem === item.id;

          return (
            <button
              key={item.id}
              onClick={() => onItemClick?.(item.id, item.path)}
              className={`w-full flex items-center space-x-3 px-4 py-3 rounded-xl text-left transition-all duration-200 ${
                isActive
                  ? 'bg-indigo-100 text-indigo-700 border-r-4 border-indigo-600'
                  : 'text-slate-600 hover:bg-slate-100 hover:text-slate-800'
              }`}
            >
              <Icon className={`h-5 w-5 ${isActive ? 'text-indigo-600' : 'text-slate-500'}`} />
              <span className="font-medium">{item.label}</span>
            </button>
          );
        })}
      </nav>

      {/* User Profile */}
      <div className="p-4 border-t border-slate-200">
        <div className="flex items-center space-x-3 p-3 bg-slate-100 rounded-xl mb-3">
          <div className="w-10 h-10 bg-indigo-600 rounded-full flex items-center justify-center">
            <User className="h-5 w-5 text-white" />
          </div>
          <div className="flex-1 min-w-0">
            <p className="text-sm font-medium text-slate-800 truncate">
              {user?.name || 'User'}
            </p>
            <p className="text-xs text-slate-500 truncate">
              {user?.email || 'user@example.com'}
            </p>
          </div>
        </div>

        <button
          onClick={handleLogout}
          className="w-full flex items-center justify-center space-x-2 px-4 py-2 border-2 border-slate-300 text-slate-600 rounded-xl hover:bg-slate-100 hover:border-slate-400 transition-colors"
        >
          <LogOut className="h-4 w-4" />
          <span className="font-medium">Logout</span>
        </button>
      </div>
    </div>
  );
};

export default ModernSidebar;
